fn find_floor(str: &str) {
    let up = '(';
    let down = ')';
    let mut current_floor: i32 = 0;

    for (idx, char) in str.chars().enumerate() {
        if current_floor == -1 {
            println!("You're in the basement! {}, {}", current_floor, idx);
            break;
        }
        
        if char == up {
            current_floor += 1;
        }

        if char == down {
            current_floor -= 1;
        }

        println!("Stepping through floor {}, {}", idx, char);
    }
}